import React, { Component } from 'react';

class PhoneForm extends Component {

    state = {
        name: '',
    }

    handleChange = (e) => {
        this.setState({
            name: e.target.value
        });
    }
    render() {
        return (
            <div>
                <input placeholder="이름" onChange={this.handleChange} value={this.state.name}/>
                <input placeholder="전화번호"/>
                {this.state.name}
            </div>
        );
    }
}

export default PhoneForm;
