import React, {Component} from 'react';
import PhoneForm from './components/PhoneForm';

class App extends Component {

  state = {
    information: [],
  }

  handleCreate = (data) => {
    console.log(data);
    this.state.information.concat(data);
  }

  render() {
    return (
      <div>
        <PhoneForm onCreate={this.handleCreate}/>
      </div>
    );
  }
}

export default App;
